mapper.select:
select B.user_id, B.user_seq_no, A.company_name, A.is_delete, to_char(coalesce(A.reg_date, now() - interval '1 year'), 'YYYY-MM-DD') as reg_date from tb_memb_info A join tb_memb_id_info B on A.user_seq_no = B.user_seq_no and B.is_delete = 'N' where 1=1
and A.memb_class = #{membClass}
or A.memb_class = 'N'
or A.memb_class = 'I'
order by A.reg_date LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
UPDATE tb_memb_info
SET memb_class=#{membClass}
	, is_delete=#{isDelete}
WHERE user_seq_no=#{userSeqNo}

UPDATE tb_menu_use_policy
SET auth_class=#{authClass}
WHERE menu_id=#{menuId}
