mapper.insert:
INSERT INTO tb_memb_info
	( memb_no
	  , memb_class
	  , company_name
	  , business_no
	  , ceo_name )
VALUES ( #{membNo}
	  , #{membClass}
	  , #{companyName}
	  , #{businessNo}
	  , #{ceoName} )

mapper.update:
UPDATE tb_memb_info
SET memb_no=#{membNo}
	, memb_class=#{membClass}
	, company_name=#{companyName}
	, business_no=#{businessNo}
	  WHERE memb_no=#{membNo}

mapper.select:
SELECT
	memb_no, memb_class, company_name, business_no, ceo_name
FROM tb_memb_info


	AND memb_no=#{membNo}


	AND business_no = #{businessNo}
